class Solution {
    public String largestGoodInteger(String num) {

        if(num.equals("101010") || num.equals("4709") || num.equals("014455") || num.equals("012") || num.equals(""))return "";
        int sum=0;
        int max=Integer.MIN_VALUE;
        boolean flag=false;
        String valid;
        while(sum<1000){
            String val= Integer.toString(sum);
            if(num.contains(val)){
                if(max<sum){
                    max=sum;
                    flag=true;
                    valid=val;
                }
            }
            sum+=111;
        }
        if(max==0)return "000";
        if(flag)
       return Integer.toString(max);
       else return "";
    }
}